mapper.update:
update verification
	set vr_code = #{vr_code}, 
		vr_failure_count = #{vr_failure_count}
	where vr_email = #{vr_email}

update member
	set mb_profile = #{mb_profile},
		mb_nickname = #{mb_nickname},
		mb_greeting = #{mb_greeting}
	where mb_num = #{mb_num}

update member
	set mb_session_id = #{mb_session_id},
		mb_session_expir = #{mb_session_expir}
	where mb_num = #{mb_num}
